                    </div>
                </div>
            </div>
            <div class="mt-12 text-center">
                <p class="text-white/50 text-sm"> 2025 $GROK. All rights reserved. This exclusive presale is only available to invited participants.</p>
                <div class="flex justify-center gap-6 mt-4"><a href="/terms-of-use" class="text-sm text-white/60 hover:text-teal-400 transition">Terms of Service</a><a href="/privacy-policy" class="text-sm text-white/60 hover:text-teal-400 transition">Privacy Policy</a></div>
            </div>
        </div>
    </footer>
</div>

<script>
    document.addEventListener("DOMContentLoaded", function () {
        const tabButtons = document.querySelectorAll('[role="tab"]');
        const tabPanels = document.querySelectorAll('[role="tabpanel"]');

        tabButtons.forEach((button) => {
            button.addEventListener("click", function () {
                //     
                tabButtons.forEach((btn) => {
                    btn.setAttribute("data-state", "inactive");
                    btn.setAttribute("aria-selected", "false");
                });

                //   
                tabPanels.forEach((panel) => {
                    panel.setAttribute("data-state", "inactive");
                    panel.hidden = true; // <-   
                });

                //   
                this.setAttribute("data-state", "active");
                this.setAttribute("aria-selected", "true");

                //   
                const panelId = this.getAttribute("aria-controls");
                const panel = document.getElementById(panelId);
                if (panel) {
                    panel.setAttribute("data-state", "active");
                    panel.hidden = false; // <-  
                }
            });
        });
    });
</script>
</body>
</html>
